import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Book } from 'src/app/interfaces/book';
import { BooksService } from 'src/app/services/books.service';
@Component({
  selector: 'app-category-details',
  templateUrl: './category-details.component.html',
  styleUrls: ['./category-details.component.scss']
})
export class CategoryDetailsComponent {
  AllBooks!:any[];
  categoryId!:number;
  currentBooks!:any[];
constructor(private _ActivatedRoute:ActivatedRoute ,private _BooksService:BooksService){
  this.getId()
}
getId(){
 const id= this._ActivatedRoute.snapshot.params['id']
//  console.log(id);
this._BooksService.getBooks().subscribe((res)=>{
  // this.AllBooks=res.data.book[0].categoryId[0]._id
  this.AllBooks=res.data.book

  for(let i=0; i<this.AllBooks.length; i++){
   this.categoryId= this.AllBooks[i]['categoryId'][0]._id
  //  console.log(this.AllBooks[i]['categoryId'][0]._id);
   if(this.categoryId==id){
    // console.log(this.AllBooks[i]);
    this.currentBooks= this.AllBooks[i]
    console.log(this.currentBooks);

   }
  }
  //  console.log(this.AllBooks);

})

}
currentPage = 1; 
itemsPerPage = 6;

}

<div>

    <div class="container">
        <h1 class="mt-5" style="text-align: center;"> BOOKS </h1>
        <br><br>
        <div>
            <div class="row" >
                <div *ngFor="let book of currentBooks  | paginate: { itemsPerPage: itemsPerPage, currentPage: currentPage }" class="col-md-4 my-3">
                    <div class="card" style="width:400px">
                        <div class="card-body">
                            <img [src]="image + '/' + book.photo" alt="title">
                        <h4 my-3>{{book.title}}</h4>
                         <p>{{book.desc}}</p>
                        </div>
                    </div>
                </div>  
            </div>
        </div>
    </div>
      </div>

      <pagination-controls class="text-center" (pageChange)="currentPage = $event"></pagination-controls>
    <app-footer></app-footer>
