import { Component } from '@angular/core';
import { CategoryService } from 'src/app/services/category.service';
@Component({
  selector: 'app-catrgories',
  templateUrl: './catrgories.component.html',
  styleUrls: ['./catrgories.component.scss']
})
export class CatrgoriesComponent {
  allCategories!:any[];
constructor(private _category:CategoryService){
  _category.getCategory().subscribe((res)=>{
    this.allCategories=res.data.categories
    // console.log( this.allCategories);

  })
}
   items = this.allCategories

currentPage = 1; 
   itemsPerPage = 5;
   getDetails(categoryId:any){
console.log(categoryId);

   }
}

<div class="container">
    <h1 class="mt-5" style="text-align: center;"> Category </h1>
    <br><br>
    <div class="portfolio">
        <div class="row" >
            <div *ngFor="let category of allCategories | paginate: { itemsPerPage: itemsPerPage, currentPage: currentPage } " class="col-md-4 my-3">
                <div class="card" (click)="getDetails(category.Id)" style="width:400px">
                    <div class="card-body">
                    <h4 my-3>{{category.Name}}</h4>
                    </div>
                </div>
            </div>  
        </div>
    </div>
</div>
<pagination-controls class="text-center mt-5" (pageChange)="currentPage = $event"></pagination-controls>

<app-footer></app-footer>
